import * as React from 'react';
import { Text, TouchableOpacity, View } from 'react-native';
import { mergeAll, flatten } from 'ramda';
import {
  buttonViewPresets,
  buttonTextPresets,
} from '../../components-ui/button/button.presets';
import { ButtonProps } from '../../components-ui/button/button.props';
import { Toast } from '../../components/common/toast/toast';
import { getI18n } from 'react-i18next';
import { CommonTranslateKey } from '../../translation/__translation-keys';

let deferButtonId2ActiveTs: any = {};
export function DeferButton(props: ButtonProps) {
  const {
    preset = 'primary',
    text,
    style: styleOverride,
    textStyle: textStyleOverride,
    children,
    disabled,
    onPress,
    id,
    ...rest
  } = props;
  const viewStyle = mergeAll(
    flatten([buttonViewPresets[preset] || buttonViewPresets.primary, styleOverride]),
  );
  const textStyle = mergeAll(
    flatten([buttonTextPresets[preset] || buttonTextPresets.primary, textStyleOverride]),
  );

  const content = children || <Text style={textStyle}>{{ text }}</Text>;

  if (disabled) {
    return (
      <View style={viewStyle} {...rest}>
        {content}
      </View>
    );
  }
  return (
    <TouchableOpacity
      activeOpacity={0.9}
      style={viewStyle}
      onPress={(e) => {
        const nowTs = new Date().getTime();
        if (id && (!deferButtonId2ActiveTs[id] || nowTs - deferButtonId2ActiveTs[id] > 60000)) {
          if (onPress) {
            onPress(e);
          }
          deferButtonId2ActiveTs[id] = nowTs;
        } else if (!id) {
          if (onPress) {
            onPress(e);
          }
        } else {
          Toast({
            message: props.deferTip || getI18n().t(CommonTranslateKey.defer_button_freq_tip),
          });
        }
      }}
      {...rest}>
      {content}
    </TouchableOpacity>
  );
}
