import { color } from 'common_ui_components/app/theme';
import * as React from 'react';
import { Switch } from 'react-native';
import { ViewStyle } from 'react-native';

/**
 * For your text displaying needs.
 *
 * This component is a HOC over the built-in React Native one.
 */
export type SwitchButtonProps = {
  disabled?: boolean;
  value?: boolean;
  color?: string;
  onValueChange?: Function | undefined;
  size?: number;
};
export function SwitchButton(props: SwitchButtonProps) {
  // grab the props
  const { disabled, value, onValueChange, size = 28, ...rest } = props;
  return (
    <Switch
      value={value}
      disabled={disabled}
      // thumbColor={color.white}
      trackColor={{
        true: rest.color ?? color.red,
        false: '',
      }}
      style={{ transform: [{ scaleX: size / 32 }, { scaleY: size / 32 }] }}
      ios_backgroundColor={disabled ? color.greye6 : color.greycc}
      onValueChange={onValueChange}
    />
  );
}
