import * as React from 'react';
import { KeyboardAvoidingView, Platform, ScrollView, StatusBar, View } from 'react-native';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { debounce } from 'lodash';
import { EventRegister } from 'react-native-event-listeners';
import moment from 'moment';
import { ScreenProps } from './screen.props';
import { offsets, presets } from './screen.presets';
import { AutoScaleStyleFitTopLeftOffset } from 'common_ui_components/app/style/ScalingUtil';

const isIos = Platform.OS === 'ios';

function ScreenWithoutScrolling(props: ScreenProps) {
  const insets = useSafeAreaInsets();
  const preset = presets.fixed;
  const style = props.style || {};
  const backgroundStyle = props.backgroundColor ? { backgroundColor: props.backgroundColor } : {};
  const insetStyle = { paddingTop: props.unsafe ? 0 : insets.top };
  const autoScaleStyle = props.scale ? AutoScaleStyleFitTopLeftOffset : {};

  return (
    <KeyboardAvoidingView
      onTouchEnd={debounce(() => EventRegister.emit('touchEvent', moment()), 500)}
      style={[preset.outer, backgroundStyle, autoScaleStyle]}
      enabled
      behavior="padding"
      // keyboardVerticalOffset={offsets[props.keyboardOffset || 'none']}
    >
      <StatusBar
        barStyle={props.statusBar || 'dark-content'}
        backgroundColor="rgba(0, 0, 0, 0)"
        translucent
      />
      <View style={[preset.inner, style, insetStyle]}>{props.children}</View>
    </KeyboardAvoidingView>
  );
}

function ScreenWithScrolling(props: ScreenProps) {
  const insets = useSafeAreaInsets();
  const preset = presets.scroll;
  const style = props.style || {};
  const backgroundStyle = props.backgroundColor ? { backgroundColor: props.backgroundColor } : {};
  const insetStyle = { paddingTop: props.unsafe ? 0 : insets.top };

  return (
    <KeyboardAvoidingView
      accessible={false}
      onTouchEnd={debounce(() => EventRegister.emit('touchEvent', moment()), 500)}
      style={[preset.outer, backgroundStyle]}
      behavior="padding"
      keyboardVerticalOffset={offsets[props.keyboardOffset || 'none']}>
      <StatusBar
        barStyle={props.statusBar || 'dark-content'}
        backgroundColor="rgba(0, 0, 0, 0)"
        translucent
      />
      <View accessible={false} style={[preset.outer, backgroundStyle, insetStyle]}>
        <ScrollView
          accessible={false}
          style={[preset.outer, backgroundStyle]}
          contentContainerStyle={[preset.inner, style]}>
          {props.children}
        </ScrollView>
      </View>
    </KeyboardAvoidingView>
  );
}

/**
 * The starting component on every screen in the app.
 *
 * @param props The screen props
 */
export function Screen(props: ScreenProps) {
  return <ScreenWithoutScrolling {...props} />;
}
