import * as React from 'react';
import { View, ViewStyle } from 'react-native';
import { itemSeparatorStyles as styles } from './item-separator.styles';

export interface ItemSeparatorProps {
  style?: ViewStyle;
}

/**
 * React.FunctionComponent for your hook(s) needs
 *
 * Component description here for TypeScript tips.
 */

export const ItemSeparatorWrap = (props: ViewStyle) => {
  const ItemSeparator: React.FunctionComponent<ItemSeparatorProps> = () => {
    return <View style={{ ...styles.WRAPPER, ...props }} />;
  };
  return ItemSeparator;
};
