import { GlobalHelper } from 'common_services_frontend';
import { CommonTranslateKey, PruModalService } from 'common_ui_components';
import * as React from 'react';
import { getI18n } from 'react-i18next';
import { Pressable, TextStyle, View, ViewStyle } from 'react-native';
import { color } from '../../../theme';
import { Text } from '../../text/text';

const CONTAINER: ViewStyle = {
  justifyContent: 'center',
};

const TEXT: TextStyle = {
  fontSize: 14,
  color: color.primary,
};

/**
 * Describe your component here
 */
export function StaffLoginControl(props: any) {
  if (GlobalHelper.getStaffSelectedAgentCode()) {
    return (
      <Pressable
        onStartShouldSetResponderCapture={(ev) => true}
        onPress={() => {
          PruModalService.showAlert(
            getI18n().t(CommonTranslateKey.staff_block_alert_title),
            getI18n().t(CommonTranslateKey.staff_block_alert_label),
          );
        }}>
        {props.children}
      </Pressable>
    );
  } else {
    return <>{props.children}</>;
  }
}
