import * as React from 'react';
import { TouchableOpacity, Text } from 'react-native';
import { storiesOf } from '@storybook/react-native';
// the action function has one argument which is the name of the action,
// this will be displayed in the actions tab in the addons panel
// action("name here")
import { action } from '@storybook/addon-actions';

// the boolean knob renders a switch which lets you toggle a value between true or false
// you call it like boolean("name here", default_value)
import { boolean, withKnobs } from '@storybook/addon-knobs';

import { StoryScreen, Story, UseCase } from '../../../storybook/views';
import { ErrorPage } from './error-page';

declare let module: any;

storiesOf('Error&Tip page', module)
  .addDecorator((fn: () => any) => <StoryScreen>{fn()}</StoryScreen>)
  .addDecorator(withKnobs)
  // .addParameters({ notes: 'This component is currently unused in-app.' })
  .add('Icon with text', () => (
    <Story>
      <UseCase text="No Content" usage="The icon for no content">
        <ErrorPage icon="no_results_matched" />
      </UseCase>
      <UseCase text="No Network Connected" usage="The icon for a no network connected">
        <ErrorPage icon="no_network_connected" />
      </UseCase>
    </Story>
  ));
