import React, { FC } from 'react';
import { TouchableOpacity, Text, Image, ViewProps, StyleProp, ImageStyle, TextStyle } from 'react-native';
import { getImages } from '../../assets';
import { Colors, Fonts } from '../../style';

type IconButtonPayload = {
  onPress?: () => void;
  icon?: number;
  iconColor?: string;
  iconStyle?: StyleProp<ImageStyle>;
  buttonSize?: number;
  buttonColor?: string;
  iconSize?: number;
  borderRadius?: number;
  borderWidth?: number;
  borderColors?: string;
};
type IconButtonProps = ViewProps & {
  payload: IconButtonPayload;
};

const IconButton: FC<IconButtonProps> = ({ payload, ...props }) => {
  const { onPress, icon, iconColor, iconStyle, iconSize, buttonSize, borderRadius, borderWidth, borderColors, buttonColor} = payload;

  const { style } = props;
  const defaultButtonSize = 48
  const defaultIconSize = 16;
  const defaultRadius = 12;
  const defaultBorderWidth= 2;
  return (
    <TouchableOpacity style={[{
      width: buttonSize ? buttonSize : defaultButtonSize,
      height: buttonSize ? buttonSize : defaultButtonSize,
      backgroundColor: buttonColor,
      alignItems: "center",
      justifyContent: 'center',
      borderRadius: borderRadius ? borderRadius : defaultRadius,
      borderWidth: borderWidth ? borderWidth : defaultBorderWidth,
      borderColor: borderColors,
    }, style]}
    onPress={onPress}
    >
      <Image source={icon ? icon : getImages.tick} style={[{
        width: iconSize ? iconSize : defaultIconSize,
        height: iconSize ? iconSize : defaultIconSize,
        tintColor: iconColor
      }, iconStyle ? iconStyle : {}]} />
    </TouchableOpacity>
  );
};

export default IconButton;
