import React, { FC } from 'react';
import { icons, IconTypes } from '../../../components/icon-font/fonts/__icontype';
import { createIconSet } from 'react-native-vector-icons';
import { IconButtonProps } from 'react-native-vector-icons/Icon';
import { TextStyle } from 'react-native';

const Icon = createIconSet(icons, 'iconfont', 'iconfont.ttf');

type VectorButtonProps = IconButtonProps & {
  name: IconTypes;
};

export const VectorButton: FC<VectorButtonProps> = ({ name, ...props }) => {
  const { iconStyle, backgroundColor, underlayColor, onPress } = props;
  return (
    <Icon.Button
      {...props}
      backgroundColor={backgroundColor ? backgroundColor : 'transparent'}
      underlayColor={underlayColor ? underlayColor : 'transparent'}
      name={name}
      onPress={onPress}
      iconStyle={{
        ...(iconStyle ? iconStyle : {}),
        marginRight: props.children ? 10 : 0,
      }}>
      {props.children}
    </Icon.Button>
  );
};
