import React, { FC, useState, useEffect, useMemo } from "react";
import {
  TouchableOpacity,
  View,
  Text,
  Image,
  ViewProps,
  StyleSheet,
  Alert,
  TextInput,
} from "react-native";
import { getImages } from "../../assets";
import CurrencyInput from 'react-native-currency-input';
import { Colors, Fonts } from '../../style';

export type PruNumberFieldInLinePayload = {
  title?: string;
  placeholder?: string;
  defaultInput?: number;
  disabled?: boolean;
  errorMessage?: string;
  validation?: (
    input: number
  ) => boolean | { isValid: boolean; errorMessage?: string };
  textInputProps?: ViewProps;
  onStatusChange: (status: NumberFieldStatus) => void;
  isPercent?: boolean;
  maxValue?: number;
};
export type PruNumberFieldInLineProps = ViewProps & {
  payload: PruNumberFieldInLinePayload;
};

enum NumberFieldStatus {
  default = "default",
  active = "active",
  error = "error",
}

type NumberFieldImageStatus = {
  [id in NumberFieldStatus]?: number;
};

type NumberFieldColorStatus = {
  [id in NumberFieldStatus]: string;
};
const backgroundColor: NumberFieldColorStatus = {
  default: Colors.greyFF,
  active: Colors.greyFF,
  error: Colors.redLight2,
};
const lineColor: NumberFieldColorStatus = {
  default: Colors.dark33,
  active: Colors.dark33,
  error: Colors.primary,
};

export function useNumberFieldInLine({ payload, ...props }: PruNumberFieldInLineProps) {
  const {
    title,
    placeholder,
    disabled,
    defaultInput,
    errorMessage,
    validation,
    maxValue,
    textInputProps,
    onStatusChange,
    isPercent,
  } = payload;

  const [status, setStatus] = useState<NumberFieldStatus>(
    NumberFieldStatus.default
  );
  const [errorMsg, setErrorMsg] = useState(errorMessage);
  const [value, setValue] = useState(defaultInput ?? null);

  useEffect(() => {
    if (onStatusChange) onStatusChange(status);
    return () => {};
  }, [status]);

  function inputValidation(value: number | null) {
    if (status == NumberFieldStatus.default && !value) {
      // skip this case which is defaultcase
    }
    else if (validation) {
      const result = validation(value ?? 0);
      if (typeof result == "boolean") {
        setStatus(
          result ? NumberFieldStatus.active : NumberFieldStatus.error
        );
      } else {
        const { isValid, errorMessage } = result;
        setStatus(
          isValid ? NumberFieldStatus.active : NumberFieldStatus.error
        );
        if (errorMessage) {
          setErrorMsg(errorMessage);
        }
      }
    }
  }

  useEffect(() => {
    inputValidation(value ?? null)
  }, [value]);

  const { style } = props;
  const textColor = disabled ? Colors.grey85 : Colors.dark33;
  const bkColor = backgroundColor[status];
  const iconSize = 20;
  const inputField = (
    <View
      style={[
        {
          flexDirection: "column",
        },
        style,
      ]}
    >
      <View
        style={{
          flexDirection: "column",
          backgroundColor: bkColor,
        }}
      >
        {title ? (
          <Text
            style={[Fonts.label.font14Bold, {
              color: Colors.dark33,
              textAlignVertical: "center",
            }]}
          >
            {title}
          </Text>
        ) : null}
        <View
          style={{
            marginTop: 2,
            flexDirection: isPercent ? "row" : "row-reverse",
          }}
        >
          <CurrencyInput
          style={[
            {
              color: textColor,
              fontSize: 28,
              height: 34,
              flex: isPercent ? 0 : 1,
              textAlign: "left",
              fontFamily: 'Montserrat',
              marginStart: 8,
            },
            textInputProps ? textInputProps : {},
          ]}
            value={value}
            onChangeValue={setValue}
            prefix=""
            delimiter=","
            separator="."
            precision={0}
            keyboardType="numeric"
            editable={!disabled}
            placeholder={placeholder}
            placeholderTextColor={Colors.greyBA}
            onChangeText={(formattedValue: string) => {
              console.log(formattedValue); // $2,310.46
            }}
            maxValue={maxValue}
            selectionColor={Colors.primary}
          />
          <Image
              source={isPercent ? getImages.percent : getImages.dollar}
              style={{
                alignSelf: "center",
                marginStart: 4,
                tintColor: Colors.primary,
                width: iconSize,
                height: iconSize,
                resizeMode: "contain",
                flex: isPercent ? 0 : 0,
              }}
            />
        </View>
        <View
          style={{
            height: 2,
            backgroundColor: lineColor[status],
            marginTop: 8,
          }}
        ></View>
      </View>
      {status === NumberFieldStatus.error && errorMsg ? (
        <Text
          style={[Fonts.label.font12Medium,{
            color: Colors.primary,
            marginTop: 8,
            alignSelf: "flex-start",
          }]}
        >
          {errorMsg}
        </Text>
      ) : null}
    </View>
  );
  return [inputField, value, status];
}
