// const fs = require('fs');
// const en = require("./locale/en").default
import fs from "fs";
import en from "./en.js";
// const i18nDir = __dirname;

const TranslateKey = {};

for (const key in en.Common) {
    if (en.Common.hasOwnProperty(key)) {
        TranslateKey[key] = "Common:" + key;
    }
}

fs.writeFileSync(
    "./app/translation/__translation-keys.ts",
    "export const CommonTranslateKey = " +
    JSON.stringify(TranslateKey).replace(/(",)|({)/g, "$&\n    ") +
    ";"
);
