import * as React from 'react';
import { Alert, View, Text } from 'react-native';
import { Story, UseCase } from 'common_ui_components/storybook/views';
import { PruUI } from 'common_ui_components';
import { icons } from 'common_ui_components/app/components/icon-font/fonts/__icontype';

export const IconButtonStory = () => (
  <Story>
    <UseCase text="Active" usage="The active icon buttons.">
      <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
        {Object.keys(icons)
          .filter((_, index) => index < 6)
          .map((icon: any) => {
            return (
              <View
                key={icon}
                style={{
                  alignItems: 'center',
                  width: 120,
                  height: 66,
                  paddingHorizontal: 8,
                  // marginVertical: 8,
                }}>
                <PruUI.IconButton
                  iconName={icon}
                  size={48}
                  onPress={() => Alert.alert(`${icon} pressed`)}
                />
              </View>
            );
          })}
      </View>
    </UseCase>
    <UseCase text="Inactive" usage="The inactive icon buttons.">
      <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
        {Object.keys(icons)
          .filter((_, index) => index < 6)
          .map((icon: any) => {
            return (
              <View
                key={icon}
                style={{
                  alignItems: 'center',
                  width: 120,
                  height: 66,
                  paddingHorizontal: 8,
                  marginVertical: 8,
                }}>
                <PruUI.IconButton
                  iconName={icon}
                  size={48}
                  // onPress={() => Alert.alert(`${icon} pressed`)}
                  disabled
                />
              </View>
            );
          })}
      </View>
    </UseCase>
  </Story>
);
