import * as React from 'react';
import { ViewStyle, TextStyle } from 'react-native';
import { storiesOf } from '@storybook/react-native';
import { StoryScreen } from '../../views';
import { DefaultButtonStory } from './default-button.story';
import { StoryName } from '../constants';
import { IconButtonStory } from './icon-button.story';
import { TextIconButtonStory } from './text-icon-button.story';
import { TagButtonStory } from './tag-button.story';
import { RoundBaseButtonStory } from './round-base-button.story';
import { TextButtonStory } from './text-button.story';
import { SwitchButtonStory } from './switch-button.story';

declare let module;

const buttonStyleArray: ViewStyle[] = [{ paddingVertical: 100 }, { borderRadius: 0 }];

const buttonTextStyleArray: TextStyle[] = [{ fontSize: 20 }, { color: '#a511dc' }];

storiesOf('Button', module)
  .addDecorator((fn) => <StoryScreen>{fn()}</StoryScreen>)
  .add(StoryName.BUTTON__DEFAULT, DefaultButtonStory)
  .add(StoryName.BUTTON__TEXT_ICON, TextIconButtonStory)
  .add(StoryName.BUTTON__ICON, IconButtonStory)
  .add(StoryName.BUTTON__TAG, TagButtonStory)
  .add(StoryName.BUTTON__ROUND_BASE, RoundBaseButtonStory)
  .add(StoryName.BUTTON__TEXT, TextButtonStory)
  .add(StoryName.BUTTON__SWITCH, SwitchButtonStory);
