import * as React from 'react';
import { SwitchProps, Text, View } from 'react-native';
import { Story, UseCase } from 'common_ui_components/storybook/views';
import { PruUI } from 'common_ui_components';
import { SwitchButtonProps } from 'common_ui_components/app/components-ui/button/switch-button';

const SwitchButton = ({ value, size }: SwitchButtonProps) => {
  const [isEnable, setEnable] = React.useState(value);
  return (
    <PruUI.SwitchButton
      value={isEnable}
      size={size}
      onValueChange={() => {
        setEnable(!isEnable);
      }}
    />
  );
};

export const SwitchButtonStory = () => (
  <Story>
    <UseCase text="On" usage="Switch on and clickable">
      <Text style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
        <SwitchButton value={true} />
      </Text>
    </UseCase>
    <UseCase text="Off" usage="Switch off but clickable">
      <Text style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
        <SwitchButton value={false} />
      </Text>
    </UseCase>
    <UseCase text="Disable" usage="unclickable">
      <Text style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
        <PruUI.SwitchButton disabled value={true} onValueChange={() => null} />
        <View style={{ width: 16 }} />
        <PruUI.SwitchButton disabled value={false} onValueChange={() => null} />
      </Text>
    </UseCase>
    <UseCase text="Adjust size" usage="size = 20 dp / 36 dp">
      <Text style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
        <SwitchButton value={true} size={20} />
        <View style={{ width: 16 }} />
        <SwitchButton value={false} size={36} />
      </Text>
    </UseCase>
  </Story>
);
