import * as React from 'react';
import { Text, View } from 'react-native';
import { Story, UseCase } from '../../views';
import { PruUI } from 'common_ui_components';
import { PresetEnum } from 'common_ui_components/app/components-ui/button/button.presets';

const TagButton = ({ selected, preset = PresetEnum.primary }) => {
  const [isSelected, setSelected] = React.useState(selected);
  return (
    <PruUI.TagButton
      preset={preset}
      selected={isSelected}
      text="Category"
      onPress={() => {
        setSelected(!isSelected);
      }}
    />
  );
};

export const TagButtonStory = () => (
  <Story>
    <UseCase text="Normal" usage="Unselected but clickable">
      <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
        <TagButton selected={false} />
        <View style={{ width: 16 }} />
        <TagButton selected={false} preset={PresetEnum.secondary} />
      </View>
    </UseCase>
    <UseCase text="Disable" usage="Unselected and unclickable">
      <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
        <PruUI.TagButton text="Category" disabled onPress={() => null} />
      </View>
    </UseCase>
    <UseCase text="Selected" usage="Selected and clickable">
      <View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
        <TagButton selected={true} />
        <View style={{ width: 16 }} />
        <TagButton selected={true} preset={PresetEnum.secondary} />
      </View>
    </UseCase>
  </Story>
);
