import * as React from 'react';
import { storiesOf } from '@storybook/react-native';
import { StoryScreen } from '../../views';
import { StoryName } from '../constants';
import { IconFontLayerStory } from './iconfont-with-background.story';
import { IconFontStory } from './iconfont.story';

declare let module;

storiesOf('Iconfont', module)
  .addDecorator((fn) => <StoryScreen>{fn()}</StoryScreen>)
